package com.base.volley.utils.logger;import com.base.volley.BuildConfig;public class Logger {		public static final String TAG = Logger.class.getSimpleName();	/**	 * The override method of Logger.	 * 	 * The default level of any tag is set to LOGLEVEL 5. This means that any	 * level log will be logged. if your set the LOGLEVEL to 0 , no log will be	 * print out.	 */	public static int LOGLEVEL = BuildConfig.DEBUG ? 5 : 0;	public static boolean VERBOSE = LOGLEVEL > 4;	public static boolean DEBUG = LOGLEVEL > 3;	public static boolean INFO = LOGLEVEL > 2;	public static boolean WARN = LOGLEVEL > 1;	public static boolean ERROR = LOGLEVEL > 0;	public static void setDebugMode(boolean debugMode) {		LOGLEVEL = debugMode ? 5 : 0;		VERBOSE = LOGLEVEL > 4;		DEBUG = LOGLEVEL > 3;		INFO = LOGLEVEL > 2;		WARN = LOGLEVEL > 1;		ERROR = LOGLEVEL > 0;	}	public static void v(String tag, String msg) {		if (DEBUG) {			LogFile.v(tag, msg == null ? "" : msg);		}	}	public static void v(String tag, String msg, Throwable tr) {		if (DEBUG) {			LogFile.v(tag, msg == null ? "" : msg, tr);		}	}	public static void v(String msg) {		if (DEBUG) {			LogFile.v(TAG, msg == null ? "" : msg);		}	}	public static void v(String msg, Throwable tr) {		if (DEBUG) {			LogFile.v(TAG, msg == null ? "" : msg, tr);		}	}	public static void d(String tag, String msg) {		if (DEBUG) {			LogFile.d(tag, msg == null ? "" : msg);		}	}	public static void d(String tag, String msg, Throwable tr) {		if (DEBUG) {			LogFile.d(tag, msg == null ? "" : msg, tr);		}	}	public static void d(String msg) {		if (DEBUG) {			LogFile.d(TAG, msg == null ? "" : msg);		}	}	public static void d(String msg, Throwable tr) {		if (DEBUG) {			LogFile.d(TAG, msg == null ? "" : msg, tr);		}	}	public static void i(String tag, String msg) {		if (DEBUG) {			LogFile.d(tag, msg == null ? "" : msg);		}	}	public static void i(String tag, String msg, Throwable tr) {		if (DEBUG) {			LogFile.d(tag, msg == null ? "" : msg, tr);		}	}	public static void i(String msg) {		if (DEBUG) {			LogFile.d(TAG, msg == null ? "" : msg);		}	}	public static void i(String msg, Throwable tr) {		if (DEBUG) {			LogFile.d(TAG, msg == null ? "" : msg, tr);		}	}	public static void w(String tag, String msg) {		if (DEBUG) {			LogFile.d(tag, msg == null ? "" : msg);		}	}	public static void w(String tag, String msg, Throwable tr) {		if (DEBUG) {			LogFile.d(tag, msg == null ? "" : msg, tr);		}	}	public static void w(String msg) {		if (DEBUG) {			LogFile.d(TAG, msg == null ? "" : msg);		}	}	public static void w(String msg, Throwable tr) {		if (DEBUG) {			LogFile.d(TAG, msg == null ? "" : msg, tr);		}	}	public static void e(String tag, String msg) {		if (ERROR) {			LogFile.e(tag, msg == null ? "" : msg);		}	}	public static void e(String tag, String msg, Throwable tr) {		if (ERROR) {			LogFile.e(tag, msg == null ? "" : msg, tr);		}	}	public static void e(String msg) {		if (ERROR) {			LogFile.e(TAG, msg == null ? "" : msg);		}	}	public static void e(String msg, Throwable tr) {		if (ERROR) {			LogFile.e(TAG, msg == null ? "" : msg, tr);		}	}}